import {useState} from 'react'
import Filter from '../components/Filter'

const OPTIONS = [
  {label: 'UX Design', value: 'UX Design projects here'},
  {label: 'Motion Design', value: 'Motion Design projects here'},
  {label: 'Website Design', value: 'Website Design projects here'},
]

const FilterPage = () => {
  // this is where we will store which option value was selected from our Filter
  const [value, setValue] = useState(null)

  const handleChange = (option) => {
    setValue(option)
  }
  return (
    <div className="flex justify-center">
      <div><Filter options={OPTIONS} onChange={handleChange}></Filter></div>
      <h1 className="p-3 main-font">{value?.value}</h1>
    </div>
  )
}

export default FilterPage
