import { ABOUT } from "../components/About-data";
import ABOUT_IMG from "../assets/Me_circle_325px.png";

const AboutPage = () => {
  return (
    <div className="justify-center grid-cols-4 gap-10">
      <div>
        <img src={ABOUT_IMG} alt="self-portrait" />
      </div>

      <div className="title">
        <h2 className="text-xl font-bold">{ABOUT.title}</h2>
        <p>{ABOUT.description}</p>
      </div>

      <div className="tools">
        <h2 className="text-xl font-bold">Ingredients</h2>
        <ul>
          {ABOUT.tools.map((item, index) => {
            return <li key={index}>{item}</li>;
          })}
        </ul>
      </div>

      <div>
        <h2 className="text-xl font-bold">Skills</h2>
        <ol>
          {ABOUT.skills.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ol>
      </div>
    </div>
  );
};

export default AboutPage;
