import React from "react";
import { useState } from "react";
import cx from "classnames";

const Filter = (props) => {
  const { options, onChange } = props;
  // isSelected = what is selected right now
  // setIsSelected = set a new selection
  // useState(null) = isSelected is null (nothing is selected) in the beginning
  const [isSelected, setIsSelected] = useState(null);

  const handleClick = (opt) => { // select an option on click whereby..
    setIsSelected(opt.label); // take the label of the option (label in the FilterPage.js)..
    onChange(opt); //and render it out (speaking to the parent)
  };

  const renderedFilters = options.map((opt, index) => {
    const active = isSelected === opt.label
    return (
      <button
        key={index}
        className={cx("rounded-full m-3 main-font",
          active
            ? "bg-blue-500 border-blue-500 text-white px-4 py-2 border mr-2 cursor-pointer"
            : "bg-white border-gray-300 text-black px-4 py-2 border mr-2 cursor-pointer"
    )}
        onClick={() => handleClick(opt)}
      >
        {opt.label}
      </button>
    );
  });

  return <div className="flex">{renderedFilters}</div>;
};

export default Filter;
